#Problem 7

count = 1
num = 3
plist = []
while count < 10001: #while loop until 10001st is found
    isprime = True #prime finding process
    for i in range(2, num): #if the number is divisible by any number between 1 and itself it is not a prime
        if num % i == 0:
            isprime = False
            break
    if isprime == False:
        num += 1
    else:
        plist.append(num) #add primes to list
        num +=1
        count +=1

print(plist[len(plist)-1]) #print last prime


#Problem 8
num = ''
#initial number

it = 0 #index

times = 0 #times it has been run
productlist = [] #list of products
while times < 1000:
    product = 1 #product that resets every iteration
    numlist = [] #empty number list for splitting numbers up into individual digits
    current = num[it:(it+13)] #chunks of 13 of the 1000 digit number
    for x in str(current): #splitting up each of the 13 digits
        numlist.append(int(x))
    for i in numlist: #multiplying each of the split up digits
        product = product * i
    productlist.append(product) #add to product list
    times += 1 #go again
    it += 1 #next chunk of 13

print(max(productlist)) #highest out of product list
    

